BEGIN TRANSACTION;
DROP TABLE IF EXISTS "금어기_테이블";
CREATE TABLE IF NOT EXISTS "금어기_테이블" (
	"이름(국명)"	TEXT NOT NULL UNIQUE,
	"금지체장"	TEXT,
	"금지체중"	TEXT,
	"수심"	TEXT,
	FOREIGN KEY("이름(국명)") REFERENCES "어류_테이블"("이름(국명)") ON DELETE CASCADE ON UPDATE CASCADE
);
DROP TABLE IF EXISTS "어류_테이블";
CREATE TABLE IF NOT EXISTS "어류_테이블" (
	"이름(국명)"	TEXT NOT NULL UNIQUE,
	"학명"	TEXT NOT NULL UNIQUE,
	"이미지"	BLOB,
	"형태"	TEXT,
	"분포"	TEXT,
	"몸길이"	TEXT,
	"서식지"	TEXT,
	"주의사항"	TEXT,
	PRIMARY KEY("이름(국명)")
);
DROP TABLE IF EXISTS "생물분류_테이블";
CREATE TABLE IF NOT EXISTS "생물분류_테이블" (
	"이름(국명)"	TEXT NOT NULL UNIQUE,
	"생물분류"	TEXT NOT NULL,
	FOREIGN KEY("이름(국명)") REFERENCES "어류_테이블"("이름(국명)") ON DELETE CASCADE ON UPDATE CASCADE
);
DROP TABLE IF EXISTS "특별_금지행정_테이블";
CREATE TABLE IF NOT EXISTS "특별_금지행정_테이블" (
	"특별_금지행정_ID"	INTEGER UNIQUE,
	"특별_금지구역"	TEXT,
	"금지시작기간"	TEXT,
	"금지종료기간"	TEXT,
	PRIMARY KEY("특별_금지행정_ID" AUTOINCREMENT)
);
DROP TABLE IF EXISTS "특별_금지행정_관계_테이블";
CREATE TABLE IF NOT EXISTS "특별_금지행정_관계_테이블" (
	"이름(국명)"	TEXT NOT NULL,
	"특별_금지행정_ID"	INTEGER NOT NULL,
	FOREIGN KEY("특별_금지행정_ID") REFERENCES "특별_금지행정_테이블"("특별_금지행정_ID") ON DELETE CASCADE ON UPDATE CASCADE,
	FOREIGN KEY("이름(국명)") REFERENCES "금어기_테이블"("이름(국명)") ON DELETE CASCADE ON UPDATE CASCADE
);
COMMIT;
